import "./ProductFilters.css";
import { useProducts } from "../../contexts/productContext";

const ProductFilters = () => {
  const {
    productState,
    productDispatch,
    filterTypes,
    toggleFilter,
    showFilter,
  } = useProducts();

  const {
    BRAND,
    CATEGORY,
    SORT_BY,
    IN_STOCK,
    FAST_DELIVERY,
    RATING,
    PRICE_RANGE,
    CLEAR_FILTERS,
  } = filterTypes;

  return (
    <aside className={`product-sidebar ${showFilter ? "show-filter" : null}`}>
      <div className="title">
        <div className="heading-3">Filters</div>

        <div className="clear-wrapper">
          <a
            className="clear-filter"
            onClick={() =>
              productDispatch({
                type: CLEAR_FILTERS,
                payload: { data: productState.products },
              })
            }
          >
            Clear
          </a>
          <button
            className="btn btn-floating btn-secondary"
            onClick={toggleFilter}
          >
            <i className="fa fa-times"></i>
          </button>
        </div>
      </div>

      <div className="filters">
        <div className="filter-wrapper">
          <div className="filter-title">
            Price: &#8377;{productState.priceRange}
          </div>
          <div className="filter-value">
            <div className="filter-price">
              <span>&#8377; 0</span>
              <span> &#8377; 1500 </span>
            </div>
            <input
              type="range"
              list="steplist"
              min="0"
              max="1500"
              step="300"
              value={productState.priceRange}
              onChange={(e) =>
                productDispatch({
                  type: PRICE_RANGE,
                  payload: { value: e.target.value },
                })
              }
            />
            <datalist id="steplist">
              <option>0</option>
              <option>300</option>
              <option>600</option>
              <option>900</option>
              <option>1200</option>
              <option>1500</option>
            </datalist>
          </div>
        </div>

        <div className="filter-wrapper">
          <div className="filter-title">Popular Brands</div>
          <div className="filter-value filter-category">
            <label>
              <input
                type="checkbox"
                checked={productState.brand.includes("FOGG")}
                value="FOGG"
                onChange={(e) =>
                  productDispatch({
                    type: BRAND,
                    payload: { value: e.target.value },
                  })
                }
              />{" "}
              Fogg
            </label>
            <label>
              <input
                type="checkbox"
                checked={productState.brand.includes("DENVER")}
                value="DENVER"
                onChange={(e) =>
                  productDispatch({
                    type: BRAND,
                    payload: { value: e.target.value },
                  })
                }
              />{" "}
              Denver
            </label>
            <label>
              <input
                type="checkbox"
                checked={productState.brand.includes("THE MAN COMPANY")}
                value="THE MAN COMPANY"
                onChange={(e) =>
                  productDispatch({
                    type: BRAND,
                    payload: { value: e.target.value },
                  })
                }
              />{" "}
              The Man Company
            </label>
            <label>
              <input
                type="checkbox"
                checked={productState.brand.includes("ENVY")}
                value="ENVY"
                onChange={(e) =>
                  productDispatch({
                    type: BRAND,
                    payload: { value: e.target.value },
                  })
                }
